import React, {Component} from 'react';
import '../node_modules/react-vis/dist/style.css';
import {XYPlot, LineSeries, VerticalGridLines, HorizontalGridLines, XAxis, YAxis} from 'react-vis';
import {
  makeWidthFlexible,
} from 'react-vis';


const Plot = ({ width, measurements }) =>
<XYPlot
height={180}
width={width}>
<VerticalGridLines />
<HorizontalGridLines />
<XAxis />
<YAxis />
<LineSeries data={measurements} />
</XYPlot>

Plot.propTypes = { width: React.PropTypes.number, measurements: React.PropTypes.array }
Plot.displayName = 'TimeSeriesLineChartPlot'
const FlexibleXYPlot = makeWidthFlexible(Plot)

export default class PercipitationGraph extends Component{

    render()
    {
        if(!this.props.selectedDistrict)
        {
            return "";
        }

        const data = [
            {x: 0, y: 8},
            {x: 1, y: 5},
            {x: 2, y: 4},
            {x: 3, y: 9},
            {x: 4, y: 1},
            {x: 5, y: 7},
            {x: 6, y: 6},
            {x: 7, y: 3},
            {x: 8, y: 2},
            {x: 9, y: 0},
            {x: 10, y: 4}
          ];
          const { measurements } = this.props
          return (
            <div className="Graph">


            <FlexibleXYPlot measurements={measurements} />

            </div>
          );

    }
}
