import React, {Component} from 'react';
import '../node_modules/react-vis/dist/style.css';
import {XYPlot, LineSeries, VerticalGridLines, HorizontalGridLines, XAxis, YAxis} from 'react-vis';
import {
  makeWidthFlexible,
} from 'react-vis';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import NativeSelect from '@material-ui/core/NativeSelect';
import InputBase from '@material-ui/core/InputBase';

import { purple } from '@material-ui/core/colors';
import Switch from '@material-ui/core/Switch';
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';

import 'date-fns';
import DateFnsUtils from '@date-io/date-fns';



const PurpleSwitch = withStyles({
  switchBase: {
    color: purple[300],
    '&$checked': {
      color: purple[500],
    },
    '&$checked + $track': {
      backgroundColor: purple[500],
    },
  },
  checked: {},
  track: {},
})(Switch);

const BootstrapInput = withStyles(theme => ({
  root: {
    'label + &': {
      marginTop: theme.spacing(3),
    },
  },
  input: {
    borderRadius: 4,
    position: 'relative',
    backgroundColor: theme.palette.background.paper,
    border: '1px solid #ced4da',
    fontSize: 16,
    padding: '10px 26px 10px 12px',
    transition: theme.transitions.create(['border-color', 'box-shadow']),
    // Use the system font instead of the default Roboto font.
    fontFamily: [
      '-apple-system',
      'BlinkMacSystemFont',
      '"Segoe UI"',
      'Roboto',
      '"Helvetica Neue"',
      'Arial',
      'sans-serif',
      '"Apple Color Emoji"',
      '"Segoe UI Emoji"',
      '"Segoe UI Symbol"',
    ].join(','),
    '&:focus': {
      borderRadius: 4,
      borderColor: '#80bdff',
      boxShadow: '0 0 0 0.2rem rgba(0,123,255,.25)',
    },
  },
}))(InputBase);

const useStyles = makeStyles(theme => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  margin: {
    margin: theme.spacing(1),
  },
}));

export default class PercipitationGraph extends Component{
    constructor(props)
    {
      super(props);
      this.state = {
        selectedTypeFrequency: "Weekly",
        selectedGraphType: false

      }

      this.handleChangeFrequency = this.handleChangeFrequency.bind(this);
      this.handleChangeGraphType = this.handleChangeGraphType.bind(this);
    }

    handleChangeFrequency(item)
    {
      console.log(item)
      this.setState({selectedTypeFrequency: item.target.value});
    }

    handleChangeGraphType(item)
    {
      console.log(item.target.checked);
      this.setState({selectedGraphType: item.target.checked});
    }

    render()
    {

        if(!this.props.selectedDistrict)
        {
            return "";
        }



        const data = [
            {x: 0, y: 8},
            {x: 1, y: 5},
            {x: 2, y: 4},
            {x: 3, y: 9},
            {x: 4, y: 1},
            {x: 5, y: 7},
            {x: 6, y: 6},
            {x: 7, y: 3},
            {x: 8, y: 2},
            {x: 9, y: 0},
            {x: 10, y: 4}

          ];

          console.log("type: "+this.state.selectedTypeFrequency)
          return (
            <div className="Graph">
              <form autoComplete="off">
              <FormGroup>
                    <Typography component="div">
                      <Grid component="label" container alignItems="center" spacing={1}>
                      <Grid item>Temperature</Grid>
                      <Grid item>
                        <PurpleSwitch
                          checked={this.state.selectedGraphType}
                          onChange={this.handleChangeGraphType}
                          value={this.state.selectedGraphType}
                        />
                       </Grid>
                       <Grid item>Perciptiation</Grid>
                       </Grid>
                    </Typography>     
                </FormGroup>
            <Grid>
              <FormControl>
                <InputLabel htmlFor="age-customized-select">Frequency</InputLabel>
                <Select
                  value={this.state.selectedTypeFrequency}
                  onChange={this.handleChangeFrequency}
                  input={<BootstrapInput name="age" id="age-customized-select" />}
                >
                  <MenuItem value={"Weekly"}>Weekly</MenuItem>
                  <MenuItem value={"Monthly"}>Monthly</MenuItem>
                  <MenuItem value={"Yearly"}>Yearly</MenuItem>

                </Select>
              </FormControl>
              </Grid>
              </form>
              <XYPlot height={300} width= {1000}>
                <VerticalGridLines />
                <HorizontalGridLines />
                <XAxis />
                <YAxis />
                <LineSeries data={data} />
            </XYPlot>
            </div>
          );

    }
}
