import React, {Component} from 'react';
import '../node_modules/react-vis/dist/style.css';
import {XYPlot, LineSeries, VerticalGridLines, HorizontalGridLines, XAxis, YAxis} from 'react-vis';
import {
  makeWidthFlexible,
} from 'react-vis';
import {getAsic} from './Districts';


export default class GraphRender extends Component{
    constructor(props)
    {
      super(props);
      this.state = {
        isLoaded:false,
        items:{}
      }
    }

    componentWillReceiveProps(newProps)
    {
      var queryData = "http://data.rcc-acis.org/StnData?sid=144972&sdate="+this.props.selectedStartDate+"&edate="+this.props.selectedEndDate+"&elems=maxt&output=json"
      fetch(queryData)
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            items: result.data
          });       
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )

    }



    render()
    {
      if(this.state.isLoaded)
      {
        return(
          <div>
            {getAsic(this.props.asicStation)}
           <XYPlot height={300} width= {1100}  xType="time" title="Average Temperature">        
                   <VerticalGridLines />
                   <HorizontalGridLines />
                   <XAxis title={"Dates from "+ this.props.selectedStartDate+" to "+ this.props.selectedEndDate}/>
                   <YAxis title="Temperature (Â°F)"/>
                   <LineSeries />
           </XYPlot>
          </div>

         );
      }else{
        return("Working on it.")
      }



    }

}
